import React, { useState } from "react";
import axios from "axios";
import "./Register.css"

export default function Register(props) {

    const [formValues, setFormValues] = useState({
        prefix:"",
        first_name:"",
        last_name:"",
        email: "",
        password: ""
    });
    const [error, setError] = useState("");

    const [loggedIn, setLoggedIn] = useState(false)

    const handleChange = (event) => {
        const { name, value } = event.target
        setFormValues({ ...formValues, [name]: value })
    }

    const createUser = () => {
        console.log('hhhhhh')
        if(formValues.email === "" || formValues.password==="" || formValues.first_name===""
        || formValues.last_name==="") {
            setError("First name , last name , email and password cannot be blank !")
        } else {
          /* axios.post(`http://localhost:8007/api/register`,
             { email : formValues.username, password :formValues.password}).then(res =>{
                if(res.data.length <= 0){
                    setError("Username or password is incorrect !")
                    } else {
                        console.log(res.data);
                        setError("");
                        props.setUser(res.data)
                        setLoggedIn(true);
                    } 
             });*/

           console.log(formValues);
        }



    }

    return (
        <div className="register-container">
            <div className="register">
                <form className="register-form" onSubmit={event => event.preventDefault()} >
                    <h2>CREATE NEW CUSTOMER ACCOUNT</h2>
                    <h3>Prefix:</h3>
                    <select name="prefix" value={formValues.prefix} onChange = {handleChange} >
                        <option value=" "> </option>
                        <option value= "Mr">Mr.</option>
                        <option value= "Mrs">Mrs.</option>
                        <option value= "Ms">Ms.</option>
                    </select>
                    <h3>First Name:</h3>
                    <input type="text" name="first_name" value={formValues.first_name} onChange = {handleChange}/>
                    <h3>Last Name:</h3>
                    <input type="text" name="last_name" value={formValues.last_name} onChange = {handleChange}/>
                    <h3>Email:</h3>
                    <input type="email" name="email" value={formValues.email} onChange = {handleChange}/>
                    <h3>Password:</h3>
                    <input type="password" name="password" value={formValues.password} onChange = {handleChange}/>
                    <br/>
                    <input type="submit" value="CREATE NEW ACCOUNT" onClick={createUser} className="register-button"/>
                </form>
            </div>       
        </div>
    )

}
