import React, { useState } from "react";
import axios from "axios";
import "./Login.css"

export default function Login(props) {

    const [formValues, setFormValues] = useState({
        username: "",
        password: ""
    });
    const [error, setError] = useState("");

    const [loggedIn, setLoggedIn] = useState(false)

    const handleChange = (event) => {
        const { name, value } = event.target
        setFormValues({ ...formValues, [name]: value })
    }

    const fetchUser = () => {
        if(formValues.name === "" || formValues.password==="") {
            setError("Username or password cannot be blank !")
        } else {
           axios.post(`http://localhost:8007/api/login`,
             { email : formValues.username, password :formValues.password}).then(res =>{
                if(res.data.length <= 0){
                    setError("Username or password is incorrect !")
                    } else {
                        console.log(res.data);
                        setError("");
                        props.setUser(res.data)
                        setLoggedIn(true);
                    } 
             });


        }



    }

    return (
        <div class="login-container">
        <div class="login">

          <div class="login-form">
            <h2>CUSTOMER LOGIN</h2>
            <br/>
            <h3>Username:</h3>
            <input type="text" placeholder="Username"/><br/>
            <h3>Password:</h3>
            <input type="password" placeholder="Password"/>
            <br/>
            <input type="button" value="LOGIN" class="login-button"/>
            <br/>
          </div>
        </div>
    </div>
    )

}
