import React, { useState } from "react";
import axios from "axios";
import "./Register.css"

export default function Register(props) {

    const [formValues, setFormValues] = useState({
        username: "",
        password: ""
    });
    const [error, setError] = useState("");

    const [loggedIn, setLoggedIn] = useState(false)

    const handleChange = (event) => {
        const { name, value } = event.target
        setFormValues({ ...formValues, [name]: value })
    }

    const fetchUser = () => {
        if(formValues.name === "" || formValues.password==="") {
            setError("Username or password cannot be blank !")
        } else {
           axios.post(`http://localhost:8007/api/login`,
             { email : formValues.username, password :formValues.password}).then(res =>{
                if(res.data.length <= 0){
                    setError("Username or password is incorrect !")
                    } else {
                        console.log(res.data);
                        setError("");
                        props.setUser(res.data)
                        setLoggedIn(true);
                    } 
             });


        }



    }

    return (
        <div className="register-container">
            <div className="register">
                <form className="register-form">
                    <h2>CREATE NEW CUSTOMER ACCOUNT</h2>
                    <h3>Prefix:</h3>
                    <input type="select">
                        <option valeur="fr">Français</option>
                        <option valeur="nl">Néerlandais</option>
                        <option valeur="en">Anglais</option>
                        <option valeur="other">Autre</option>
                    </input>
                    <h3>First Name:</h3>
                    <input type="text" />
                    <h3>Last Name:</h3>
                    <input type="text" />
                    <h3>Email:</h3>
                    <input type="email"/>
                    <h3>Password:</h3>
                    <input type="password" />
                    <br/>
                    <input type="submit" value="CREATE NEW ACCOUNT" className="register-button"/>
                </form>
            </div>       
        </div>
    )

}
