import React, { useState } from "react";
import axios from "axios";


export default function Login(props) {

    const [formValues, setFormValues] = useState({
        username: "",
        password: ""
    });
    const [error, setError] = useState("");

    const [loggedIn, setLoggedIn] = useState(false)

    const handleChange = (event) => {
        const { name, value } = event.target
        setFormValues({ ...formValues, [name]: value })
    }

    const fetchUser = () => {
        if(formValues.name === "" || formValues.password==="") {
            setError("Username or password cannot be blank !")
        } else {
            axios.post(`http://localhost:8007/api/login`,
             { username : formValues.username, password :formValues.password}).then(res =>{
                if(res.data.length <= 0){
                    setError("You must register first !")
                    } else {
                        console.log(res.data);
                        setError("");
                        props.setUser(res.data[0])
                        setLoggedIn(true);
                    } 
             });
        }



    }

    return (
        <div className="container">
            <div className="row">
                <div className="span4 offset4 well">
                    <legend>Please Sign In</legend>
                    {error && <div className="alert alert-error">
                        {error}
                    </div>}
                    <form onSubmit={event => event.preventDefault()}>
                        <input type="text" id="username"  value ={formValues.username} onChange = {handleChange} className="span4" name="username" placeholder="Username" />
                        <input type="password" id="password" value= {formValues.password} onChange = {handleChange} className="span4" name="password" placeholder="Password"/>
                        <label className="checkbox">
                            <input type="checkbox" name="remember" value="1" /> Remember Me
                        </label>
                        <button type="submit" name="submit" onClick={fetchUser} className="btn btn-info btn-block">Sign in</button>
                    </form>    
                </div>
            </div>
        </div>
    )

}
