import React, { useState } from "react";
import axios from "axios";
import "./Login.css"

export default function Login(props) {

    const [formValues, setFormValues] = useState({
        username: "",
        password: ""
    });
    const [error, setError] = useState("");

    const [loggedIn, setLoggedIn] = useState(false)

    const handleChange = (event) => {
        const { name, value } = event.target
        setFormValues({ ...formValues, [name]: value })
    }

    const fetchUser = () => {
        if(formValues.name === "" || formValues.password==="") {
            setError("Username or password cannot be blank !")
        } else {
           axios.post(`http://localhost:8007/login`,
             { username : formValues.username, password :formValues.password}).then(res =>{
                if(res.data.length <= 0){
                    setError("Username or password is incorrect !")
                    } else {
                        console.log(res.data);
                        setError("");
                        props.setUser(res.data[0])
                        setLoggedIn(true);
                    } 
             });

            axios.post("http://localhost:8007/").then(res => console.log(res.data)).catch(err => console.log(err))
        }



    }

    return (
        <div className="login-container">
            <div className="login">  
                <form className="login-form" onSubmit={event => event.preventDefault()} >
                    <h2>CUSTOMER LOGIN</h2>
                    <br/>
                    {error && <div className="alert-error">
                    {error}
                    </div>}
                    <h3>Username:</h3>
                    <input type="text" name="username" value ={formValues.username} onChange = {handleChange}  placeholder="Username"/><br/>
                    <h3>Password:</h3>
                    <input type="password" name="password" value= {formValues.password} onChange = {handleChange}  placeholder="Password"/>
                    <br/>
                    <input type="submit" value="LOGIN" onClick={fetchUser} className="login-button"/>
                    <br/>
                </form>
            </div>
        </div>
    )

}
